INSERT INTO `universitas` (`id`, `nama`, `keterangan`) VALUES
(1, 'Universitas Muhammadiyah Kalimantan Timur', 'Universitas Muhammadiyah Kalimantan Timur berdiri berdasarkan SK No 463/KPT/I/2017 yang dikeluarkan oleh Kementerian Riset, Teknologi dan Perguruan Tinggi, merupakan merger Sekolah Tinggi Ilmu Ekonomi dan Sekolah Tinggi Ilmu Kesehatan Muhammadiyah Samarinda.'),
(2, 'Universitas Tujuh Belas Agustus Samarinda', 'Universitas 17 Agustus 1945 Samarinda, atau dikenal dengan UNTAG Samarinda adalah sebuah perguruan tinggi swasta yang berlokasi di Jalan Ir.H. Juanda, Kota Samarinda, Kalimantan Timur, Indonesia. Universitas ini berdiri pada tanggal 7 Oktober 1963 di bawah naungan Yayasan Pendidikan Universitas 17 Agustus 1945.'),
(3, 'Universitas Mulawarman', 'Universitas Mulawarman, disingkat Unmul, adalah perguruan tinggi negeri di Samarinda, Kalimantan Timur, Indonesia. Universitas ini berdiri pada tanggal 27 September 1962, sehingga merupakan universitas tertua di Kalimantan Timur.'),
(4, 'Universitas Indonesia', 'UI adalah salah satu universitas riset atau institusi akademik terkemuka di dunia yang terus mengejar pencapaian tertinggi dalam hal penemuan, pengembangan dan difusi pengetahuan secara regional dan global. \r\n\r\nDengan prestasi yang terus diraihnya UI berada di peringkat kampus terbaik di Indonesia berdasarkan penilaian Lembaga pemeringkatan dunia.'),
(6, 'Universitas Borneo Tarakan', 'Universitas Borneo Tarakan disingkat sebagai UBT adalah sebuah perguruan tinggi negeri yang berkedudukan di Kota Tarakan, Kalimantan Utara. Kampus utama Universitas Borneo, terletak di Jl. Amal Lama No.1 Tarakan.');
